function over(kind, j, i, extraX, extraY)
{
    //console.log("( " + ((j - 1) * 300 + (extraX * 300) - 1/2 * CCC.width) + ", " + ((i - 1) * 300 + (extraY * 300) - 1/2 * CCC.height) + " )");
    var tileX = ((j - 1) * 300 + (extraX * 300) - 1/2 * CCC.width);
    var tileY = ((i - 1) * 300 + (extraY * 300) - 1/2 * CCC.height);
    //XXX.drawImage(sea, (j - 1) * 300 + (extraX * 300) + X, (i - 1) * 300 + (extraY * 300) + Y, 300, 300);

    //PLAYER LANDSCAPE SENSING
    if (-X < tileX + 300 && -X > tileX && -Y < tileY + 300 && -Y > tileY)
    {
        if (kind == "sea")
        {
            player.land = false;
            player.water = true;
        }
        else if (kind == "landing")
        {
            player.land = true;
            player.water = true;
        }
        else if (kind == "tollBridge")
        {
            player.land = true;
            player.water = true;
            player.toll = true;
        }
    }

    //UNIT LANDSCAPE SENSING
    for (var i = 0; i < ArtificialIntelligenceAccess.length; i++)
    {
        if (-ArtificialIntelligenceAccess[i].X < tileX + 300 && -ArtificialIntelligenceAccess[i].X > tileX && -ArtificialIntelligenceAccess[i].Y < tileY + 300 && -ArtificialIntelligenceAccess[i].Y > tileY)
        {
            if (kind == "sea")
            {
                ArtificialIntelligenceAccess[i].water = true;
                ArtificialIntelligenceAccess[i].land = false;
            }
            else if (kind == "landing" || kind == "tollBridge")
            {
                ArtificialIntelligenceAccess[i].water = true;
                ArtificialIntelligenceAccess[i].land = true;
            }
        }
    }

}

//This function sets the lower canvas X and Y to un-problematic values whilst the mouse is in the main inventory.
function lowerCanvasMouseCoordsReset()
{
    lMouseX = -1000; //this makes it so when you are not in the lower canvas it does not try to set your mouses position, which can accidentally click something you didn't want to.
    lMouseY = 1000;
}

function mainCanvasMouseCoordsReset()
{
    mouseX = Math.cos(player.rotation - 1/2 * Math.PI) * 500000;
    mouseY = Math.sin(player.rotation - 1/2 * Math.PI) * 500000;
}

//This is the function that sets the difference in time that allows the game to maintain the same appearance of speed even when the gameloop is too slow or fast.
function timeTracker(time)
{
    if (beegin == "start")
    {
        TTP = time;
        beegin = null;
    }
    TTN = time;
    TTD = TTN - TTP;
    TTP = time;
}

